import React from "react";
import { SearchContext } from "../context/searchContext";
import Article from "./Article";
// import Spinner from "./Spinner";
import ErrorMessage from "./ErrorMessage";

export default class Articles extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      showError: false,
    };

    this.articleList = this.articleList.bind(this);
  }

  checkVisited(title, list, dumb) {
    console.log(dumb);
    return list.includes(title) ? true : false;
  }

  articleList = (articles, list, dumb) => {
    if (articles.length > 0) {
      return articles.map((details, index) => {
        return (
          <Article
            title={details.title}
            image={details.image}
            description={details.description}
            url={details.url}
            key={index}
            visited={this.checkVisited(details.title, list, dumb)}
          />
        );
      });
    } else {
      return <ErrorMessage />;
    }
  };

  render() {
    return (
      <SearchContext.Consumer>
        {({ articlesFromGNews, articleTitlesFromMongo, dumbarr }) => (
          <div className="d-flex flex-wrap justify-content-evenly my-3">
            {this.articleList(
              articlesFromGNews,
              articleTitlesFromMongo,
              dumbarr
            )}
          </div>
        )}
      </SearchContext.Consumer>
    );
  }
}
